/*
    这个文件用于创建一个为Count组件服务的reducer，reducer的本质是一个函数
*/

const InitState = 100
export default function countReducer(preState=InitState,action){
    const {type,value} = action
    switch(type){
        case 'increment':
            return preState+value;
        case 'decrement':
            return preState-value;
        default:
            return preState
    }

}
