import {INCREMENT,DECREMENT} from '../Constant'
export const increament=value=>({type:INCREMENT,value})
export const decreament=value=>({type:DECREMENT,value})


// 异步action 实质就是action的值为函数  异步action会调用同步action
// 异步action不是必须要写的
export const increamentAsync= (value,time)=>{
    return (
        (dispatch)=>{
            setTimeout(()=>{
                dispatch(increament(value))
            },time)
        }
    )
}
